// App.jsx
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      newTodo: '',
      todos: [
        'Schoonmaken',
        'Boodschappen doen',
        'Espanol Oefeningen',
        'podcast Luisteren'
      ]
    };
  }

  handleChange = ({ target: { value } }) => {
    this.setState({ newTodo: value });
  };

  addNewTodo(e) {
    e.preventDefault();

    this.state.newTodo &&
      this.setState({
        todos: [...this.state.todos, this.state.newTodo],
        newTodo: ''
      });
  }

  removeTodo(index) {
    this.setState({
      todos: [...this.state.todos.filter((_value, i) => i !== index)]
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <form
            className="todo-new"
            noValidate
            onSubmit={(e) => this.addNewTodo(e)}
          >
